    const agent6 = new ConwayAgent(THREE,id++,this.world,0.1,-12,0.5,-10,0,0,-Math.PI/2,params,true);
    const agent7 = new ConwayAgent(THREE,id++,this.world,0.1,-12,1.5,-10,0,0,-Math.PI/2,params,true);
    const agent8 = new ConwayAgent(THREE,id++,this.world,0.1,-12,3,-10,0,0,-Math.PI/2,params,true);
    const agent9 = new ConwayAgent(THREE,id++,this.world,0.1,-13,0.5,-15,0,0,-Math.PI/2,params,true);
    const agent10 = new ConwayAgent(THREE,id++,this.world,0.1,-13,1.5,-15,0,0,-Math.PI/2,params,true);
