export class Agent {
  constructor(context,x,y,z,rotx=0,roty=0,rotz=0) {
    this.THREE = context;
    this.x = x;
    this.y = y;
    this.z = z;
    this.rotx = rotx;
    this.roty = roty;
    this.rotz = rotz;

    this.mesh = this.createMesh();
  }

  createMesh() {
    const geometry = new this.THREE.SphereGeometry(0.1, 24, 24,0,Math.PI*2,0,Math.PI/2);
    const material = new this.THREE.MeshBasicMaterial({
      color: 0xFFFFFF,
    });
    const mesh = new this.THREE.Mesh(geometry, material);
    mesh.position.copy(new this.THREE.Vector3(this.x,this.y,this.z));
    mesh.rotation.x = this.rotx;
    mesh.rotation.y = this.roty;
    mesh.rotation.z = this.rotz;

    return mesh;
  }

  getMesh() {
    return this.mesh;
  }

}
