import worker from "./AgentWorker"
import WebWorker from "./workerSetup"

export class Agent {
  constructor(context,world,size,x,y,z,rotx=0,roty=0,rotz=0) {
    this.THREE = context;
    this.size = size;
    this.x = x;
    this.y = y;
    this.z = z;
    this.rotx = rotx;
    this.roty = roty;
    this.rotz = rotz;
    this.state = 0;

    this.createMesh();

    this.worker = new WebWorker(worker);
    this.worker.postMessage({command:"init"});
  }

  createMesh() {
    this.geometry = new this.THREE.SphereGeometry(this.size, 24, 24,0,Math.PI*2,0,Math.PI/2);
    this.material = new this.THREE.MeshBasicMaterial({
      color: 0xFFFFFF,
    });
    this.mesh = new this.THREE.Mesh(this.geometry, this.material);
    this.mesh.position.copy(new this.THREE.Vector3(this.x,this.y,this.z));
    this.mesh.rotation.x = this.rotx;
    this.mesh.rotation.y = this.roty;
    this.mesh.rotation.z = this.rotz;
  }

  getMesh() {
    return this.mesh;
  }

  // Update rendering attributes
  updateRender() {
  }
}
