export class Agent {
  constructor(context,size,x,y,z,rotx=0,roty=0,rotz=0) {
    this.THREE = context;
    this.size = size;
    this.x = x;
    this.y = y;
    this.z = z;
    this.rotx = rotx;
    this.roty = roty;
    this.rotz = rotz;
    this.state = 0;

    this.createMesh();
  }

  createMesh() {
    this.geometry = new this.THREE.SphereGeometry(this.size, 24, 24,0,Math.PI*2,0,Math.PI/2);
    this.material = new this.THREE.MeshBasicMaterial({
      color: 0xFFFFFF,
    });
    this.mesh = new this.THREE.Mesh(this.geometry, this.material);
