import {Agent} from "./Agent";

/*
Agent based on Conway's Game of Life
 */
export class ConwayAgent extends Agent {
  constructor(context,id,world,size,x,y,z,rotx=0,roty=0,rotz=0,params,alive) {
    super(context,id,world,size,x,y,z,rotx,roty,rotz);

    this.params = params;
    this.setState(alive);
    this.state.lastExec = new Date().getTime();
  }

  setState(alive) {
    this.state.alive = alive;

    if (alive) {
      this.material.color.setHex(0x0000FF);
    } else {
      this.material.color.setHex(0x222222);
    }
  }

  // Simulation methods
  loop() {
    let time = new Date().getTime();

    if (time < this.state.lastExec + this.params.cycleTime) return;

    // Query our neighbors.  
  }
}
