export class Agent {
  constructor(context,x,y,z) {
    this.THREE = context;
    this.x = x;
    this.y = y;
    this.z = z;

    this.mesh = this.createMesh();
  }

  createMesh() {
    const geometry = new this.THREE.BoxGeometry(2, 2, 2);
    const material = new this.THREE.MeshPhongMaterial({
      color: 0x156289,
      emissive: 0x072534,
      side: this.THREE.DoubleSide,
      flatShading: true
    });
    const mesh = new this.THREE.Mesh(geometry, material);
    mesh.position.copy(new this.THREE.Vector3(this.x,this.y,this.z));

    return mesh;
  }

  getMesh() {
    return this.mesh;
  }

}
